import React, { useState } from 'react'





export default function TextForm(props) {
    const handleUpClick = () =>{
        // console.log('Uppercase was Clicked');
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
    }
    const handleTitleClick = (text) => {
        text = text.toLowerCase().split(' ');
        for (var i = 0; i < ext.length; i++) {
            text[i] = text[i].charAt(0).toUpperCase() + text[i].slice(1); 
        }
  return text.join(' ');
        }
    const handleClrClick = () => {
        let newText = "";
        setText(newText);
    }
    const handleOnChange = (event) =>{
        // console.log('On change');
        setText(event.target.value)
    }

    const [text, setText] = useState('');
    
  return (
    <>
        <div className='container mb-40'>
            <h2 className="mb-4 my-4">{props.heading}</h2>
            <div className="mb-3">
            <textarea className="form-control" placeholder="Enter Your Text" value={text} onChange={handleOnChange} id="myBox" rows="10"></textarea>
            </div>
            <button className="btn btn-primary me-2" onClick={handleUpClick}>UpperCase</button>
            <button className="btn btn-primary me-2" onClick={handleLowClick}>LowerCase</button>
            <button className="btn btn-primary me-2" onClick={handleTitleClick}>Title</button>
            <button className="btn btn-primary me-2" onClick={handleClrClick}>Clear</button>
        </div>
        <div className="container my-4">
            <h1>Your Text Summary</h1>
            <p>{text.split(" ").length} Words and {text.length} Characters</p>
            <p>{0.008 * text.split(" ").length} minutes to read</p>
            <h2>Preview Text</h2>
            <p>{text}</p>
        </div>
    </>
  )
}
