import React, { useState } from 'react'

export default function TextForm(props) {
    const handleUpClick = () =>{
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
    }    
    const handleCopy = () => {
        let text = document.getElementById('myBox');
        text.select();
        navigator.clipboard.writeText(text.value);
    }
    const handleExtraSpaces = () =>{
        let newText = text.split(/[ ]+/);
        setText(newText.join(" "))
    }
    const handleClrClick = () => {
        let newText = "";
        setText(newText);
    }
    const handleOnChange = (event) =>{
        setText(event.target.value)
    }

    const [text, setText] = useState('');
    
  return (
    <>
        <div className='container mb-40'>
            <div className='row'>
                <div className='col-md-6'>
                    <h3 className={`mb-4 my-4 fw-bold text-${props.mode === 'light'? 'dark' : 'light'}`}>{props.heading}</h3>
                    <div className="mb-3">
                     <textarea className="form-control" placeholder="Enter Your Text" style={{backgroundColor: props.mode === 'light'? 'dark' : 'light'}} value={text} onChange={handleOnChange} id="myBox" rows="10"></textarea>
                    </div>
                    <h6 className={`fw-bold mb-4 text-${props.mode === 'light'? 'dark' : 'light'}`}>Your Text Summary {text.split(" ").length} Words and {text.length} Characters {0.008 * text.split(" ").length} minutes to read</h6>
                    <button className="btn btn-primary me-2" onClick={handleUpClick}>UpperCase</button>
                    <button className="btn btn-warning me-2" onClick={handleLowClick}>LowerCase</button>
                    <button className="btn btn-success me-2" onClick={handleCopy}>Copy Text</button>
                    <button className="btn btn-info me-2" onClick={handleExtraSpaces}>Remove Extra Spaces</button>
                    <button className="btn btn-danger me-2" onClick={handleClrClick}>Clear</button>
                </div>
                <div className='col-md-6'>
                 <h3 className={`mb-4 my-4 fw-bold text-${props.mode === 'light'? 'dark' : 'light'}`}>Preview Text</h3>
                 <p className={`text-${props.mode === 'light'? 'dark' : 'light'}`}>{text}</p>
                </div>
            </div>
        </div>
    </>
  )
}
