import React, { useState } from 'react'





export default function TextForm(props) {
    const handleUpClick = () =>{
        // console.log('Uppercase was Clicked');
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLowClick = () => {
        let newText = text.toLowerCase();
        setText(newText);
    }
    const handleSentClick = () => {
        let newText = text.toProperCase();
        setText(newText);
    }
    const handleOnChange = (event) =>{
        // console.log('On change');
        setText(event.target.value)
    }

    const [text, setText] = useState();
    
  return (
    <div>
        <h2 className="mb-4 my-4">{props.heading}</h2>
        <div className="mb-3">
         <textarea className="form-control" placeholder="Enter Your Text" value={text} onChange={handleOnChange} id="myBox" rows="10"></textarea>
        </div>
        <button className="btn btn-primary me-2" onClick={handleUpClick}>AA</button>
        <button className="btn btn-primary me-2" onClick={handleLowClick}>aa</button>
        <button className="btn btn-primary me-2" onClick={handleSentClick}>Aa</button>
    </div>
  )
}
